pragma solidity ^0.5.0;

import './Algorithm.sol';
//  
//  
//  
contract Main {
    address payable organizer;      // 
    uint public nodeNumber;         // 
    uint public totalTaskNumber;    // 
    uint public successTaskNumber;  // 
    uint public finalMatrixSum;     // 
    uint public matrixLength;       // 
    uint public nowTaskNode;        //  successTaskNumber 

    struct Participant {
        address addr;
        uint ability;   // 
        bool available; // , :true(), false
    }
    Participant[] public Nodes; // 
    event testTask(uint x);     // x

    constructor() public {
        // 
        organizer = msg.sender;
        nodeNumber = 1;
        matrixLength = 20;  // 
        totalTaskNumber = matrixLength * 20;
        successTaskNumber = 0;
        finalMatrixSum = 0;
        nowTaskNode = 0;
    }

    function init()
    public{
        successTaskNumber = 0;
        finalMatrixSum = 0;
        nowTaskNode = 0;
    }

    //  unitLoad -  
    function matrixMulitexecWork(uint unitLoad, uint _matrixLength)
    private
    pure
    returns(uint) {
        //
        if(unitLoad == 0){
            return 0;
        }
        uint Length = _matrixLength;
        uint[] memory x = new uint[](Length);
        uint[] memory y = new uint[](Length);
        uint[] memory result = new uint[](unitLoad);    // 
        for(uint i = 0; i < Length; i++){
            x[i] = 1;
            y[i] = 1;
        }
        uint sum = 0;
        for(uint i = 0; i < unitLoad; i++){
            //  i < Length
            // 
            //  j < 10
            uint r = 0;
            //  10*10 
            for(uint j = 0; j < 10; j++){
                for(uint k = 0; k < 10; k++){

                }
                r = x[j]*y[j] + r;
            }
            sum = sum + r;
            result[i] = r;
        }
        return sum;
    }

    /*
        0 
         organizer 
    */
    function no_distribute_manager()
    public
    returns(uint){
        require(msg.sender == organizer);
        uint unitLoad = totalTaskNumber;
        uint sum = matrixMulitexecWork(unitLoad, matrixLength);
        //  finalMatrixSum
        finalMatrixSum = finalMatrixSum + sum;
        //  successTaskNumber
        successTaskNumber = successTaskNumber + unitLoad;
        return sum;
    }

    /*
        
        
    */
    function direct_distribute_manager()
    public
    returns(uint){
        uint[] memory ability = direct_distribute(totalTaskNumber);
        uint unitLoad = getLoadByAddress(ability, msg.sender);  // 
        uint sum = matrixMulitexecWork(unitLoad, matrixLength);
        //  finalMatrixSum
        finalMatrixSum = finalMatrixSum + sum;
        //  successTaskNumber
        successTaskNumber = successTaskNumber + unitLoad;
        //
        if(successTaskNumber == totalTaskNumber){
            return finalMatrixSum;
        }
        //
        return 0;
    }

    /*
        
        
        
    */
    function loop_distribute_manager()
    public
    returns(uint){
        uint[] memory ability = loop_distribute(totalTaskNumber);
        uint unitLoad = getLoadByAddress(ability, msg.sender);
        uint task_remained = totalTaskNumber - successTaskNumber;
        while(true){
            if(successTaskNumber >= totalTaskNumber){
                return finalMatrixSum;
            }
            task_remained = totalTaskNumber - successTaskNumber;
            if(unitLoad > task_remained){
                unitLoad = task_remained;
            }
            //  successTaskNumber
            uint sum = matrixMulitexecWork(unitLoad, matrixLength);
            successTaskNumber = successTaskNumber + unitLoad;

            //  finalMatrixSum
            finalMatrixSum = finalMatrixSum + sum;
        }
    }


    function setAvailableByAddr(address addr, bool flag)
    public{
        uint length = Nodes.length;
        for(uint i = 0; i < length; i++){
            if(Nodes[i].addr == addr){
                Nodes[i].available = flag;
            }
        }
    }

    function taskFinished()
    public{
        successTaskNumber++;
    }


    // 
    function better_cnp_manager(bool flag)
    public
    returns(uint){
        //  available  true
        setAvailableByAddr(msg.sender, true);
        // index
        uint index = cnp_distribute(flag);
        nowTaskNode = index;
        //  available  false
        setAvailableByAddr(msg.sender, false);

        if(successTaskNumber >= totalTaskNumber){
            return finalMatrixSum;
        }

        //  finalMatrixSum
        finalMatrixSum = finalMatrixSum + 10;

        return 0;
    }

    // flag = ture => B
    // flag = false => A
    function cnp_distribute(bool flag)
    public
    returns(uint){
        uint Length = nodeNumber;
        uint[] memory ability = new uint[](Length);
        uint maxIndex = 1;
        for(uint k = 0; k < Length; k++){
            if(Nodes[k].available){
                maxIndex = k;
                break;
            }
        }
        for(uint i = 0; i < Length; i++){
            ability[i] = Nodes[i].ability;
            if(ability[maxIndex] < ability[i] && Nodes[i].available){
                maxIndex = i;
            }
        }
        if(flag){
            // 
            ability[2] = ability[2]/100;
            ability[3] = ability[3]/100;
            for(uint j = 0; j < Length; j++){
                if(ability[maxIndex] < ability[j]){
                    maxIndex = j;
                }
            }
        }
        //  Tasks
        nowTaskNode = maxIndex;
        return maxIndex;
    }

    /*
        
    */
    function direct_distribute_without_testTask_manager()
    public
    returns(uint){
        uint[] memory ability = direct_distribute(totalTaskNumber);
        uint unitLoad = getLoadByAddress(ability, msg.sender);  // 
        uint sum = matrixMulitexecWork(unitLoad, matrixLength);
        //  finalMatrixSum
        finalMatrixSum = finalMatrixSum + sum;
        //  successTaskNumber
        successTaskNumber = successTaskNumber + unitLoad;
        //
        if(successTaskNumber == totalTaskNumber){
            return finalMatrixSum;
        }
        //
        return 0;
    }

    /*
        
        
        @param uint totalTaskNum - 
        @returns uint[] memory - 
    */
    function loop_distribute_without_testTask_manager()
    public
    returns(uint){
        // 50
        uint unitLoad = matrixLength;
        uint task_remained = totalTaskNumber - successTaskNumber;
        while(true){
            if(successTaskNumber >= totalTaskNumber){
                return finalMatrixSum;
            }
            task_remained = totalTaskNumber - successTaskNumber;
            if(unitLoad > task_remained){
                unitLoad = task_remained;
            }
            //  successTaskNumber
            uint sum = matrixMulitexecWork(unitLoad, matrixLength);
            successTaskNumber = successTaskNumber + unitLoad;

            //  finalMatrixSum
            finalMatrixSum = finalMatrixSum + sum;
        }
    }

    /*
      
      
      
      100      ms
      400      ms
      900      ms
      2500     ms
      3600     ms
      10000 out of gas
    */
    function direct_distribute(uint totalTaskNum)
    public
    returns(uint[] memory){
        Algorithm algo = new Algorithm();
        uint Length = nodeNumber;
        uint[] memory ability = new uint[](Length);
        uint maxIndex = 0;
        for(uint i = 0; i < Length; i++){
            ability[i] = Nodes[i].ability;
            if(ability[maxIndex] < ability[i]){
                maxIndex = i;
            }
        }
        //ability 
        uint GCD = algo.countNGcd(ability, Length); // 
        uint K = 0;
        for(uint i = 0; i < Length; i++){
            ability[i] = ability[i]/GCD;
            K = K + ability[i];
        }
        uint sum = 0;
        // TODO: 
        //
        if(K <= totalTaskNum){
            uint Mk = algo.getMk(K, totalTaskNum);
            uint diff = uint((Mk - K)/Length);
            for(uint i = 0; i < Length; i++){
                uint a = ability[i]+diff;
                uint b = totalTaskNum / Mk;
                ability[i] = a * b;
                sum = sum + ability[i];
            }
        } else {
            uint diff = uint((K - totalTaskNum)/Length);
            for(uint i = 0; i < Length; i++){
                uint a = ability[i]-diff;
                sum = sum + a;
                ability[i] = a;
            }
        }
        //  uint diff = uint((K - totalTaskNum)/Length) 
        if(sum >= totalTaskNum){
            ability[maxIndex] = ability[maxIndex] - (sum - totalTaskNum);
        }else{
            ability[maxIndex] = ability[maxIndex] + (totalTaskNum - sum);
        }

        return ability;
    }

    /*
        
        
        
        @param uint totalTaskNum - 
        @returns uint[] memory - 
    */
    function loop_distribute(uint task_remained)
    public
    view
    returns(uint[] memory){
        uint Length = nodeNumber;
        uint[] memory ability = new uint[](Length);
        uint K = 0;
        uint maxIndex = 0;
        for(uint i = 0; i < Length; i++){
            ability[i] = Nodes[i].ability;
            K = K + ability[i];
            if(ability[maxIndex] < ability[i]){
                maxIndex = i;
            }
        }
        // TODO: 
        //
        if(K > task_remained){
            uint diff = uint((K - task_remained)/Length);
            uint sum = 0;
            for(uint i = 0; i < Length; i++){
                uint a = ability[i]-diff;
                sum = sum + a;
                ability[i] = a;
            }
            //  uint diff = uint((K - totalTaskNum)/Length) 
            if(sum >= task_remained){
                ability[maxIndex] = ability[maxIndex] - (sum - task_remained);
            }else{
                ability[maxIndex] = ability[maxIndex] + (task_remained - sum);
            }
        }
        return ability;
    }

    /*
        
        
        @param uint totalTaskNum - 
        @returns uint[] memory - 
    */
    function direct_distribute_without_testTask(uint totalTaskNum)
    public
    view
    returns(uint[] memory){
        uint Length = nodeNumber;
        uint[] memory ability = new uint[](Length);
        uint average = uint(totalTaskNum / Length);
        for(uint i = 0; i < Length; i++){
            ability[i] = average;
        }
        // average
        uint t = average * Length;
        if(t <= totalTaskNumber){
            ability[0] = ability[0] + (totalTaskNumber - t);
        }else{
            ability[0] = ability[0] - (t - totalTaskNumber);
        }
        return ability;
    }

    function getLoadByAddress(uint[] memory ability, address addr)
    private
    view
    returns(uint){
        uint Length = nodeNumber;
        for(uint i = 0; i < Length; i++){
            if(Nodes[i].addr == addr){
                return ability[i];
            }
        }
    }

    function getAbility() public
    view
    returns(uint[] memory){
        uint Length = nodeNumber;
        uint[] memory ability = new uint[](Length);
        for(uint i = 0; i < Length; i++){
            ability[i] = Nodes[i].ability;
        }
        return ability;
    }

    function showNode(address addr) public
    view
    returns(address, uint, bool){
        uint length = Nodes.length;
        bool flag = false;
        Participant memory node;
        for(uint i = 0; i < length; i++){
            if(Nodes[i].addr == addr){
                node = Nodes[i];
                flag = true;
            }
        }
        return (node.addr, node.ability, node.available);
    }

    function connect() public returns(uint){   // solidity
        Participant memory _participant = Participant(msg.sender, 0, false);
        uint length = Nodes.length;
        //
        for(uint i = 0; i < length; i++){
            if(Nodes[i].addr == msg.sender){
//                emit testTask(10000);
                return 10000;
            }
        }
        Nodes.push(_participant);
        nodeNumber = Nodes.length;
        //
//        emit testTask(10000);
        return 10000;
    }

    function disconnect(address addr) public{
        uint length = Nodes.length;
        for(uint i = 0; i < length; i++){
            if(Nodes[i].addr == addr){
                Nodes[i] = Nodes[length-1];
                Nodes.pop();
            }
        }
        nodeNumber = Nodes.length;
    }

    function setAbility(uint ability) public{
        uint length = Nodes.length;
        for(uint i = 0; i < length; i++){
            if(Nodes[i].addr == msg.sender){
                Nodes[i].ability = ability;
            }
        }
    }

    // 
    function kill() public{
        if (organizer == msg.sender) {
            selfdestruct(organizer);
        }
    }

}
